package com.infra.corejava;

public class ForLoopExample1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i = 0; i < 10; i++) {
			System.out.println("hello world");
		}
		int i =0;
		while(i < 10) {
			System.out.println("hello world1");
			i++;
		}

	}

}
